glsl4.vert:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
};
layout(location = 0) in vec2 a_position;
layout(location = 1) in vec2 a_texCoord;
layout(location = 0) out vec2 v_uv;
layout(location = 1) out vec4 v_offset;
vec4 vert () {
  vec4 pos = vec4(a_position, 0, 1);
  v_uv = a_texCoord * cc_screenScale.xy;
  v_offset = v_uv.xyxy + cc_nativeSize.zwzw * vec4(1.0, 0.0, 0.0, -1.0);
  return pos;
}
void main() { gl_Position = vert(); }

glsl4.frag:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
};
vec3 ACESToneMap (vec3 color) {
  color = min(color, vec3(8.0));
  const float A = 2.51;
  const float B = 0.03;
  const float C = 2.43;
  const float D = 0.59;
  const float E = 0.14;
  return (color * (A * color + B)) / (color * (C * color + D) + E);
}
layout(location = 0) in vec2 v_uv;
layout(location = 1) in vec4 v_offset;
layout(set = 1, binding = 0) uniform sampler2D u_texSampler;
layout(set = 1, binding = 1) uniform sampler2D u_blendTexSampler;
vec3 ToLDR(vec3 color) {
  #if CC_USE_HDR
    color *= cc_exposure.x * 1024.0;
    color = ACESToneMap(color);
    color = sqrt(color);
  #endif
  return color;
}
vec4 frag () {
  #if CC_USE_SMAA
    vec4 a;
    a.rb = texture(u_blendTexSampler, v_uv).rb;
    a.g = texture(u_blendTexSampler, v_offset.zw).g;
    a.a = texture(u_blendTexSampler, v_offset.xy).a;
    if (dot(a, vec4(1.0)) < 1e-5) {
      vec4 o = texture(u_texSampler, v_uv);
      o.rgb = ToLDR(o.rgb);
      return o;
    } else {
      vec2 offset;
      offset.x = a.a > a.b ? a.a : -a.b;
      offset.y = a.g > a.r ? -a.g : a.r;
      if (abs(offset.x) > abs(offset.y)) {
        offset.y = 0.0;
      } else {
        offset.x = 0.0;
      }
      vec4 C = texture(u_texSampler, v_uv);
      C.rgb = ToLDR(C.rgb);
      vec2 uv = v_uv + sign(offset) * cc_nativeSize.zw;
      vec4 Cop = texture(u_texSampler, uv);
      Cop.rgb = ToLDR(Cop.rgb);
      float s = abs(offset.x) > abs(offset.y) ? abs(offset.x) : abs(offset.y);
      C.rgb = pow(C.rgb, vec3(2.2));
      Cop.rgb = pow(Cop.rgb, vec3(2.2));
      vec4 mixed = mix(C, Cop, s);
      mixed.rgb = pow(mixed.rgb, vec3(1.0 / 2.2));
      return mixed;
    }
  #else
    vec4 o = texture(u_texSampler, v_uv);
    o.rgb = ToLDR(o.rgb);
    return o;
  #endif
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

